package javaPrograms;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// In a java program we store the data in a Variable
		// Variables are temporary memory location where you can store data temporarily
		// how do you write a variable
		// every statement in java ends with ;
		// every variable we have to declare its datatype
		// different datatypes: int, double, char, String, boolean
		// syntax:  datatype variablename = value
		int var1 = 10;   // integer
		double var2 = 2.3;   // decimal value
		char var3 = 'A';   // character
		String var4 = "Selenium";  //string
		boolean var5 = true;  // boolean

		// write the statement to print the variable value
		
		System.out.println("The value of variable var1: " + var1);
		System.out.println("The value of variable var2: " + var2);
		System.out.println("The value of variable var3: " + var3);
		System.out.println("The value of variable var4: " + var4);
		System.out.println("The value of variable var5: " + var5);
		
		
		
	}

}

